# import gem and C libraryrequire 'ruby2d'require 'ffi'# import classesrequire_relative 'robot'require_relative 'bomb'module Driver  extend FFI::Library  ffi_lib 'driver/acesso.so'  attach_function :readDriver, [], :pointerend# config windowset title: 'Robot'set width: 800, height: 600set background: '#1A1A1A'music = Music.new('assets/song.mp3')music.playmusic.loop = truerobot = Robot.new(x: 362.5, y: 270)bombs = []update do  clear  robot.draw  bombs << Bomb.new(x: rand(720), y: rand(720)) if (rand(1000) % 333).zero?  bombs.each(&:draw)  pointer = Driver::readDriver  puts pointer.read_string  robot.walk(pointer.read_string)  bombs.delete_if { |b| robot.hasBomb(b) }endshow